import gql from 'graphql-tag';
import { Query } from './../Query';

export type Country = {
  englishName: string;
  localName: string;
  code: string;
  regions: Region[];
};

export type Region = {
  id: number;
  code: string;
  name: string;
}

export type CountriesData = {
  items: Country[];
};

export const GET_COUNTRIES = gql`
  query Countries {
    countries {
      items {
        englishName
        localNam
        code
        regions {
          id
          code
          localName: name
          englishName: name
        }
      } 
    }
  }
`;

export class CountriesQuery extends Query<CountriesData> {
  static defaultProps = {
    query: GET_COUNTRIES
  };
}
